var http = require("https");

var options = {
  "method": "GET",
  "hostname": "api.ibm.com",
  "port": null,
  "path": "/api/v2/secrets?limit=0&offset=0",
  "headers": {
    "bluemix-org": "string",
    "correlation-id": "string",
    "authorization": "string",
    "bluemix-space": "string",
    "content-type": "application/vnd.ibm.kms.secret+json"
  }
};

var req = http.request(options, function (res) {
  var chunks = [];

  res.on("data", function (chunk) {
    chunks.push(chunk);
  });

  res.on("end", function () {
    var body = Buffer.concat(chunks);
    console.log(body.toString());
  });
});

req.end();

